import React, { Component } from 'react';
import {
     newGraph,mainArr
} from '../../util/directedGraph';



class DeliveryCost extends Component {
    constructor(props) {
        super(props);
        this.state={
            objectGraph :{},
        }
        this.googleInput = React.createRef();
    }

    componentDidMount(){

        mainArr.map((item) => {
            var route = item.substring(0, 2);
            var cost = item.substring(2);
            this.state.objectGraph[route] = Number(cost);
        });

    }

    calcCost() {
        var routesArr = [];
        var cost = 0;
        const routeGiven = this.googleInput.value;
        for (var i = 0; i < routeGiven.length - 1; i++) {
            var newStr = '';
            newStr = routeGiven.charAt(i) + routeGiven.charAt(i + 1);
            routesArr.push(newStr);
            cost = cost + newGraph[newStr];
        }
        console.log('routesArr', routesArr)

        if (isNaN(cost)) {
            cost = 'No such Route';
        }

        console.log(cost)
        return cost;
    }

    render() {
        return (
            <div>
                <h3> Calculate Delivery Cost</h3>
                <label> Route : </label>
                <input type="text" ref={(googleInput) => { this.googleInput = googleInput }} />
                <button type="submit" onClick={this.calcCost}>Calculate Cost</button>

            </div>

        )


    }
}

export default DeliveryCost;
