import React, { Component } from 'react';

import  {
    parsedInput
} from '../../util/directedGraph';
import 
    getPossiblePaths
from '../../util/possiblePath';
class PossibleRoutes extends Component{
    constructor(props){
        super(props);
        this.state={
            routes : '',
            onlyPossibleRoutesArr:[],
        }
        this.srcInput = React.createRef();
        this.destInput = React.createRef();
        this.getPossibleNodes = this.getPossibleNodes.bind(this);
        this.getRoutes = this.getRoutes.bind(this);
    }

    getPossibleNodes(){
        var routes =  getPossiblePaths(this.srcInput.value, this.destInput.value, parsedInput);
        const routesArr = routes.split(';');
        const onlyPossibleRoutesArr  = [];

        routesArr.map((route, i) => {
            const routePerNode = route.split('=');
            const formedRoute = this.getRoutes(routePerNode[0]);
            onlyPossibleRoutesArr.push(formedRoute);
            onlyPossibleRoutesArr.push(formedRoute);
            return routePerNode;
        });

        this.setState({ onlyPossibleRoutesArr: onlyPossibleRoutesArr})
    }

    getCheapestRoute() {
        var routes = getPossiblePaths(this.srcInput.value, this.destInput.value, parsedInput);
        const routesArr = routes.split(';');
        const onlyPossibleRoutesArr = [];

        routesArr.map((route, i) => {
            const routePerNode = route.split('=');
            const formedRoute = this.getRoutes(routePerNode[0]);
            onlyPossibleRoutesArr.push(formedRoute);
            return routePerNode;
        });

        this.setState({ onlyPossibleRoutesArr: onlyPossibleRoutesArr })
    }


    getRoutes(str) {
        var newStr = '';

        for (var i = 0; i <= str.length - 1; i++) {
            let sym = '';
            if (i !== str.length - 1) {
                console.log(i)
                sym = "-"
            }

            var append = str[i] + sym;
            newStr = newStr + append;
        }
        return newStr;
    }

    render(){
        let routesFormed = '';
        console.log(this.state.onlyPossibleRoutesArr);
        if(this.state.onlyPossibleRoutesArr.length >0){
            routesFormed = this.state.onlyPossibleRoutesArr.map((route,index) => {
                return <li key={route.toString()} >Route No {index+1} : {route}</li>;
            })
        }

        return(
                <div>
                <h3> Calculate​ ​the​ ​number​ ​of​ ​possible​ ​delivery​ ​route​</h3>
                <div> 
                <label>Source : </label>
                    <input type="text" ref={(srcInput) => this.srcInput = srcInput} />
                </div>
                <div> 
                    <label> Destination: </label>
                    <input type="text" ref={(destInput) => this.destInput = destInput} />
                </div>

                <button type="button" onClick={this.getPossibleNodes.bind(this)} >
                    Find All PossibleRoutes
                </button>
                <button type="button" onClick={this.getPossibleNodes.bind(this)} >
                    Find All PossibleRoutes
                </button>

                {this.state.onlyPossibleRoutesArr.length >  0 && <div>

                    <div>We found {this.state.onlyPossibleRoutesArr.length} Routes for you.</div>
                    <div>The number of PossibleRoutes are : {routesFormed}</div>
                     </div>}
                </div>


        )   
    }
}

export default PossibleRoutes;
