import React, { Component } from 'react';
import getPossiblePaths from '../../util/possiblePath';
class CheapestDeliveryRoute extends Component {
    constructor(props) {
        super(props)
        this.state = {

        }
    }

    getCheapestDeliveryPath() {
        var routes = getPossiblePaths(this.srcInput.value, this.destInput.value, parsedInput);
        const routesArr = routes.split(';');
        const onlyPossibleArr = [];

        routesArr.map((route, i) => {
            const routePerNode = route.split('=');
            const formedRoute = this.getRoutes(routePerNode[1]);
            onlyPossibleArr.push(formedRoute);
            return routePerNode;
        });

        this.setState({ onlyPossibleArr: onlyPossibleArr })
    }


    render() {


        return (
            <div>
                <h3> Calculate​ ​the​ Cheapest Delivery Path</h3>
                <div>
                    <label>Source : </label>
                    <input type="text" ref={(srcInput) => this.srcInput = srcInput} />
                </div>
                <div>
                    <label> Destination: </label>
                    <input type="text" ref={(destInput) => this.destInput = destInput} />
                </div>
                <button type="button" onClick={this.getCheapestDeliveryPath.bind(this)} >
                    Find All Cheapest Delivery Route
            </button>


            </div>
        )
    }
}


export default CheapestDeliveryRoute;
