import React, { Component } from 'react';
import {
    weightedGraph, availableRoutes
} from '../../util/directedGraph';
// import Input from '../Input/Input';
// import Button from '../Button/Button';

class DeliveryCost extends Component {
    constructor(props) {
        super(props);
        this.state={
            weightedGraph: weightedGraph,
            availableRoutes: availableRoutes,
        }
        this.googleInput = React.createRef();
        this.getElementFromArr = this.getElementFromArr.bind(this);
        this.calcCost = this.calcCost.bind(this);
    }


    getElementFromArr(str) {
        console.log(str);
        this.state.weightedGraph.forEach((item) => {
            console.log(item.indexOf(str))
            if (item.indexOf(str) !== -1) {
                console.log(item)
                return item;
            }
        });

        return false;

    }
    calcCost(stnCodes) {
        console.log('hiiii')
        console.log(this.googleInput.value)
        stnCodes = stnCodes.toUpperCase();
        var cost = 0;

        for (var i = 0; i < stnCodes.length - 1; i++) {
            var newStr = '';
            newStr = stnCodes.charAt(i) + stnCodes.charAt(i + 1);
            if (this.state.onlyRoutesArr.includes(newStr)) {
                var item = this.getElementFromArr(newStr);
                console.log('eee', item)
                cost = cost + Number(item.substring(2));
            } else {
                cost = 0;
            }
        }

        if (cost === 0) {
            cost = "No such Route";
        }
        return cost;
    }

    // const costRoute = calcCost('abe');
    // console.log(costRoute);

    render() {
        return (
            <div>
                    <h3> Calculate Delivery Cost</h3>
                    <label> Route : </label>
                    <input type="text" ref={(googleInput) => { this.googleInput = googleInput }} />
                    <button type="submit" onClick={this.calcCost}>Calculate Cost</button>

            </div>

        )


    }
}

export default DeliveryCost;
