import React, { Component } from 'react';
import {
    mainArr
} from '../../util/directedGraph';



class DeliveryCost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            objectGraph: {},
            routeCost:0
        }
        this.googleInput = React.createRef();
    }

    componentDidMount() {
        var obj = {};
        mainArr.map((item) => {
            var route = item.substring(0, 2);
            var cost = item.substring(2);
            obj[route] = Number(cost);
            return obj;
        });

        console.log(obj);
        this.setState({ objectGraph: obj })

    }

    calcCost() {
        var routesArr = [];
        var cost = 0;
        const routeGiven = this.googleInput.value;
        for (var i = 0; i < routeGiven.length - 1; i++) {
            var newStr = '';
            newStr = routeGiven.charAt(i) + routeGiven.charAt(i + 1);
            routesArr.push(newStr);
            cost = cost + this.state.objectGraph[newStr];
            this.setState({ routeCost: cost});
        }
        console.log('routesArr', routesArr)

        if (isNaN(cost)) {
            cost = 'No such Route';
            this.setState({ routeCost: cost });
        }

        console.log(cost)
        return cost;
    }

    render() {
        return (
            <div>
                <h3> Calculate Delivery Cost</h3>
                <label> Route : </label>
                <input type="text" ref={(googleInput) => { this.googleInput = googleInput }} />
                <button type="submit" onClick={this.calcCost.bind(this)}>Calculate Cost</button>
                {this.state.routeCost >0 && (<label> Route  Cost: {this.state.cost}</label>)}
            </div>

        )


    }
}

export default DeliveryCost;
