import React, { Component } from 'react';

import {
    parsedInput
} from '../../util/directedGraph';
import
getPossiblePaths
    from '../../util/possiblePath';
class PossibleRoutes extends Component {
    constructor(props) {
        super(props);
        this.state = {
            routes: '',
            onlyPossibleRoutesArr: [],
            routesArr: '',
            cheapestRoute: '',
            showCheapestRoutes: false,
        }
        this.srcInput = React.createRef();
        this.destInput = React.createRef();
        this.getPossibleNodes = this.getPossibleNodes.bind(this);
        this.getRoutes = this.getRoutes.bind(this);
        this.getCheapestRoute = this.getCheapestRoute.bind(this);
        this.showCheapestRoutes = this.getCheapestRoute.bind(this);
    }


    getPossibleNodes(maxStops) {
        console.log(maxStops)
        var routes = getPossiblePaths(this.srcInput.value, this.destInput.value, parsedInput);
        const routesArr = routes.split(';');
        const onlyPossibleRoutesArr = [];
        const onlyRoutesWithMaxFourST = [];
        this.setState({ routesArr: routes });
        console.log("ROUTES",routes)

        routesArr.map((route, i) => {
            const routePerNode = route.split('=');
            console.log(maxStops === 4);
            if (maxStops === 4 && routePerNode[0].length <= 5){
                const routeWithFourStops = this.getRoutes(routePerNode[0]);
                onlyRoutesWithMaxFourST.push(routeWithFourStops);
                this.setState({ onlyPossibleRoutesArr: onlyRoutesWithMaxFourST })
            }else{
                const formedRoute = this.getRoutes(routePerNode[0]);
                onlyPossibleRoutesArr.push(formedRoute);
                this.setState({ onlyPossibleRoutesArr: onlyPossibleRoutesArr })
            }
            return routePerNode;
        });
       this.getCheapestRoute(routesArr);
                console.log('only 4 stops', onlyRoutesWithMaxFourST)
    }


    getCheapestRoute(routes) {
        var routeCostArr = [];
        routes.map((item) => {

            item = item.split('=');
            var route = item[0];
            var costs = item[1];
            var totalCost = 0;

            costs = costs.split(':');
            for (var i = 0; i < costs.length; i++) {
                totalCost += Number(costs[i]);
            }

            var routeCostObj = { 'path': route, 'cost': totalCost };
            routeCostArr.push(routeCostObj);

            // sort by value
            routeCostArr.sort(function (a, b) {
                return a.cost - b.cost;
            });

            console.log(routeCostArr);
            this.setState({ cheapestRoute: routeCostArr[0] })
            return routeCostArr;

        })

    }

    showCheapestRoutes(){
        this.setState({ showCheapestRoutes : true})
    }


    getRoutes(str) {
        var newStr = '';
        for (var i = 0; i <= str.length - 1; i++) {
            let sym = '';
            if (i !== str.length - 1) {
                sym = "-"
            }

            var append = str[i] + sym;
            newStr = newStr + append;
        }
        return newStr;
    }

    render() {
        let routesFormed = '';
        if (this.state.onlyPossibleRoutesArr.length > 0) {
            routesFormed = this.state.onlyPossibleRoutesArr.map((route, index) => {
                return <li key={route.toString()} >Route No {index + 1} : {route}</li>;
            })
        }


        return (
            <div>
                <h3> Calculate​ ​the​ ​number​ ​of​ ​possible​ ​delivery​ ​route​</h3>
                <div>
                    <label>Source : </label>
                    <input type="text" ref={(srcInput) => this.srcInput = srcInput} />
                </div>
                <div>
                    <label> Destination: </label>
                    <input type="text" ref={(destInput) => this.destInput = destInput} />
                </div>
                <div>
                    <button type="button" onClick={this.getPossibleNodes.bind(this,'no')} >
                        Find All PossibleRoutes
                    </button>
                    <button type="button" onClick={this.getPossibleNodes.bind(this, 4)}>
                        Find All PossibleRoutes with 4 max stops
                    </button>
                </div>
                <div>
                    <button type="button" onClick={this.showCheapestRoutes} >
                        Find the Cheapest Route
                </button>
                </div>

                {this.state.onlyPossibleRoutesArr.length > 0 && <div>
                    <div>We found {this.state.onlyPossibleRoutesArr.length} Routes for you.</div>
                    <div>The number of PossibleRoutes are : {routesFormed}</div>
{/** ≈                <div>The number of PossibleRoutes with 4 station max are : {routesFormedWithFourSt}</div>
                */}                </div>}

                {this.state.showCheapestRoutes&& <div>
                    <div>We found the cheapest route for you.</div>
                    <div>Take {this.state.cheapestRoute.path} which will cost you {this.state.cheapestRoute.cost}</div>
                </div>}

            </div>


        )
    }
}

export default PossibleRoutes;
