import React, { Component } from 'react';
import {
    mainArr
} from '../../util/directedGraph';
import './style.css';
import { Container, Form, ButtonToolbar, Button, Row, Col, FormLabel } from 'react-bootstrap';



class DeliveryCost extends Component {


    constructor(props) {
        super(props);
        this.state = {
            objectGraph: {},
            routeCost: 0,
            routesArr: [],
        }
        this.pathInput = React.createRef();
    }

    componentDidMount() {
        var obj = {};
        mainArr.map((item) => {
            var route = item.substring(0, 2);
            var cost = item.substring(2);
            obj[route] = Number(cost);
            return obj;
        });
        this.setState({ objectGraph: obj });
    }

    calcCost() {
        var routesArr = [];
        var cost = 0;
        const routeGiven = this.pathInput.value;
        for (var i = 0; i < routeGiven.length - 1; i++) {
            var newStr = '';
            newStr = routeGiven.charAt(i) + routeGiven.charAt(i + 1);
            routesArr.push(newStr);
            cost = cost + this.state.objectGraph[newStr];
            this.setState({ routesArr: routesArr });
            if (isNaN(cost)) {
                cost = 'No such Route';
            }
            this.setState({ routeCost: cost });
        }
        return cost;
    }

    render() {
        return (
            <div className="costModule">


                <Container>

                    <FormLabel> Calculate Delivery Cost</FormLabel>
                    <Row>

                        <Col xs={1}><FormLabel>Route : </FormLabel></Col>
                        <Col xs={3}><Form.Control size="md" type="text" placeholder="Path" ></Form.Control>
                            <input type="text" ref={(pathInput) => { this.pathInput = pathInput }} /></Col>
                        <Col xs={8}></Col>
                    </Row>
                    <br />
                    <Row>
                        <Col xs={1}></Col>
                        <Col xs={2}>
                            <ButtonToolbar>
                                <Button variant="primary" size="md" active onClick={this.calcCost.bind(this)}>
                                    Primary button
                                </Button>
                            </ButtonToolbar>
                        </Col>
                        <Col xs={9}></Col>

                    </Row>

                    {this.state.routeCost != 0 && <FormLabel>Route  Cost: {this.state.routeCost}</FormLabel>}
                </Container>





            </div>




        )
    }
}

export default DeliveryCost;


