export default function getPossiblePaths(start, end, inputGraph, visitedNode = '', cost = 0) {
    const edges = inputGraph[start];


    if (visitedNode && start === end) {
        // We found the path
        console.log('we found the path'+ visitedNode + end + '=' + cost)
        return visitedNode + end + '=' + cost;
    }

    if (!edges) {
        console.log('we hit deadend , all nodes visited')
        return '';
    }
     if (visitedNode.indexOf(visitedNode[visitedNode.length - 1] + start) >= 0) {
        console.log('visitedNode if present index', visitedNode);
        console.log(' already visited route we dont allow since it ll go in infiinte cycle');
        return '';
    }


    visitedNode += start;

    return Object.keys(edges).map((node) => {
        console.log('NODE',node,edges);
        // No 0 cost routes should be included as per requirements
        if (edges[node] === 0) {
            return '';
        }  
        const route = getPossiblePaths(node, end, inputGraph, visitedNode, cost + edges[node] +':');

        return route;
    }).filter((empty) => !!empty).join(';'); // Filter the routes (empty str) which had deadend
}
