export const inputRoutes = ['AB1', 'AC4', 'AD10', 'BE3', 'CD4', 'CF2', 'DE1', 'EB3', 'EA2', 'FD1'];
export const onlyRoutes = inputRoutes.map((item) => { return item.substring(0, 2) });
var onlyStartPos = inputRoutes.map((item) => { return item.substring(0, 1); });
var graphArr = {};

let uniqueonlyStartPos = onlyStartPos.filter((item, i, ar) => ar.indexOf(item) === i);

uniqueonlyStartPos.forEach((startPos) => {
    graphArr[startPos] = [];
    inputRoutes.forEach((route) => {
        if (route.charAt(0) === startPos) {
            var newObj = {};
            newObj[route.substring(1, 2)] = route.substring(2, route.length);
            graphArr[startPos].push(newObj);
        }
    })
});

const getAllNodesObject = (obj) => {
    var finalNodeObj = {};
    Object.keys(obj).map((key) => {
        var eachObj = obj[key];
        var eachPair = {};
        for (var i = 0; i < eachObj.length; i++) {
            // eslint-disable-next-line 
            Object.keys(eachObj[i]).map((key) => {
                eachPair[key] = eachObj[i][key]
            })
        }
        finalNodeObj[key] = eachPair;
        return finalNodeObj;
    })
    return finalNodeObj;
};


export const parsedInput = getAllNodesObject(graphArr);
