export const mainArr = ['AB1', 'AC4', 'AD10', 'BE3', 'CD4', 'CF2', 'DE1', 'EB3', 'EA2', 'FD1'];
export const onlyRoutes = mainArr.map((item) => {
    return item.substring(0, 2);
})

var onlyStartPos = mainArr.map((item) => {
    return item.substring(0, 1);
});



let uniqueonlyStartPos = onlyStartPos.filter((item, i, ar) => ar.indexOf(item) === i);
var graphArr = {};
uniqueonlyStartPos.forEach((startPos) => {
    graphArr[startPos] = [];
    mainArr.forEach((route) => {
        if (route.charAt(0) === startPos) {
            var newObj = {};
            newObj[route.substring(1, 2)] = route.substring(2, route.length);
            graphArr[startPos].push(newObj);
        }
    })
});




const convertArrayToObject = (obj) => {
    var finalNeededObj = {};
    Object.keys(obj).map((key) => {
        var eachObj = obj[key];
        var eachPair = {};
        for (var i = 0; i < eachObj.length; i++) {
            // eslint-disable-next-line 
            Object.keys(eachObj[i]).map((key) => {
                eachPair[key] = eachObj[i][key]

            })
        }
        finalNeededObj[key] = eachPair;
    })
    return finalNeededObj;
};


export const parsedInput = convertArrayToObject(graphArr);
