import React, { Component } from 'react';
import {
    mainArr
} from '../../util/directedGraph';
import './style.css';



class DeliveryCost extends Component {
    constructor(props) {
        super(props);
        this.state = {
            objectGraph: {},
            routeCost: 0,
            routesArr: [],
        }
        this.pathInput = React.createRef();
    }

    componentDidMount() {
        var obj = {};
        mainArr.map((item) => {
            var route = item.substring(0, 2);
            var cost = item.substring(2);
            obj[route] = Number(cost);
            return obj;
        });
        this.setState({ objectGraph: obj });
    }

    calcCost() {
        var routesArr = [];
        var cost = 0;
        const routeGiven = this.pathInput.value;
        for (var i = 0; i < routeGiven.length - 1; i++) {
            var newStr = '';
            newStr = routeGiven.charAt(i) + routeGiven.charAt(i + 1);
            routesArr.push(newStr);
            cost = cost + this.state.objectGraph[newStr];
            this.setState({ routesArr: routesArr });
            if (isNaN(cost)) {
                cost = 'No such Route';
            }
            this.setState({ routeCost: cost });
        }
        return cost;
    }

    render() {
        return (
            <div className="costModule">
                <h3> Calculate Delivery Cost</h3>
                <label> Route : </label>
                <input type="text" ref={(pathInput) => { this.pathInput = pathInput }} />
                <div>
                    <button type="button" className="btn btn-primary" onClick={this.calcCost.bind(this)}>Calculate Cost</button>
                </div>
                {this.state.routesArr.length > 0 && (<div>
                    <label> Route  Cost: {this.state.routeCost} </label>
                </div>)}
            </div>
        )
    }
}

export default DeliveryCost;
